    LANGUAGE plpgsql
    AS $$
#variable_conflict use_column
begin
  return query
  select
    id,
    content,
    metadata,
    (embedding::text)::jsonb as embedding,
    1 - (english.embedding <=> query_embedding) as similarity
  from english
  where metadata @> filter
  order by english.embedding <=> query_embedding
  limit match_count;
end;
$$;


